.text {
  position: relative;
  margin: 0;
  padding: 0;
  font-family: "Cubano", monaco, courier;
  font-size: var(--font-size, 120px);
  font-weight: 400;
  text-align: center;
  font-style: normal;
  color: aquamarine;
  text-shadow: 
    -1px 0 #4b6b00, 0 1px #4b6b00, 1px 0 #4b6b00, 0 -1px #4b6b00,
    -8px 8px #4b6b00, -7px 7px #4b6b00, -6px 6px #4b6b00, -5px 5px #4b6b00,
    -4px 4px #4b6b00, -3px 3px #4b6b00, -2px 2px #4b6b00, -1px 1px #4b6b00;
}

.colorfun-text {
  transition: all 0.5s;
  -webkit-text-stroke: 4px #d6f4f4;
  font-variation-settings: "wght" 900, "ital" 1;
  font-size: 15rem;
  text-align: center;
  color: transparent;
  font-family: "Meta", sans-serif;
  text-shadow: 
    10px 10px 0px #07bccc,
    15px 15px 0px #e601c0,
    20px 20px 0px #e9019a,
    25px 25px 0px #f40468,
    45px 45px 10px #482896;
  cursor: pointer;
}

.sweet-title {
  order: 2;
  color: #fde9ff;
  font-weight: 900;
  text-transform: uppercase;
  font-size: clamp(3rem, 10vw, 6rem);
  line-height: 0.75em;
  text-align: center;
  text-shadow: 
    3px 1px 1px #4af7ff, 2px 2px 1px #165bfb, 4px 2px 1px #4af7ff,
    3px 3px 1px #165bfb, 5px 3px 1px #4af7ff, 4px 4px 1px #165bfb,
    6px 4px 1px #4af7ff, 5px 5px 1px #165bfb, 7px 5px 1px #4af7ff,
    6px 6px 1px #165bfb, 8px 6px 1px #4af7ff, 7px 7px 1px #165bfb,
    9px 7px 1px #4af7ff;
}

.sweet-title span {
  display: block;
  position: relative;
}

.sweet-title span:before {
  content: attr(data-text);
  position: absolute;
  text-shadow: 
    2px 2px 1px #e94aa1, -1px -1px 1px #c736f9,
    -2px 2px 1px #e94aa1, 1px -1px 1px #f736f9;
  z-index: 1;
}

@font-face {
  font-family: 'Rocher';
  src: url(https://assets.codepen.io/9632/RocherColorGX.woff2);
}

.color-font {
  font-family: 'Rocher';
  text-align: center;
  font-size: 50px;
}

@font-palette-values --Grays {
  font-family: Rocher;
  base-palette: 9;
}

@font-palette-values --Purples {
  font-family: Rocher;
  base-palette: 6;
}

@font-palette-values --Mint {
  font-family: Rocher;
  base-palette: 7;
}

.grays {
  font-palette: --Grays;
}

.purples {
  font-palette: --Purples;
}

.mint {
  font-palette: --Mint;
}

.retro-neon {
  position: relative;
  font-family: 'Exo', sans-serif;
  font-size: 9em;
  margin: 0;
  transform: skew(-15deg);
  letter-spacing: 0.03em;
}

.retro-neon::after {
  content: '';
  position: absolute;
  top: -0.1em;
  right: 0.05em;
  width: 0.4em;
  height: 0.4em;
  background: 
    radial-gradient(white 3%, rgba(255, 255, 255, 0.3) 15%, rgba(255, 255, 255, 0.05) 60%, transparent 80%),
    radial-gradient(rgba(255, 255, 255, 0.2) 50%, transparent 60%) 50% 50% / 5% 100%,
    radial-gradient(rgba(255, 255, 255, 0.2) 50%, transparent 60%) 50% 50% / 70% 5%;
  background-repeat: no-repeat;
}

.retro-neon-stroke {
  display: block;
  text-shadow: 0 0 0.1em #8ba2d0, 0 0 0.2em black, 0 0 5em #165ff3;
  -webkit-text-stroke: 0.06em rgba(0, 0, 0, 0.5);
}

.retro-neon-fill {
  position: absolute;
  left: 0;
  top: 0;
  background-image: linear-gradient(
    #032d50 25%, #00a1ef 35%, white 50%,
    #20125f 50%, #8313e7 55%, #ff61af 75%
  );
  -webkit-text-stroke: 0.01em #94a0b9;
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
}
